import{_ as e,d as a}from"./app.a2dbceb3.js";const r={},i=a('<h1 id="\u5783\u573E\u6536\u96C6\u7B56\u7565\u4E0E\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5783\u573E\u6536\u96C6\u7B56\u7565\u4E0E\u7B97\u6CD5" aria-hidden="true">#</a> \u5783\u573E\u6536\u96C6\u7B56\u7565\u4E0E\u7B97\u6CD5</h1><p>\u7A0B\u5E8F\u8BA1\u6570\u5668\u3001\u865A\u62DF\u673A\u6808\u3001\u672C\u5730\u65B9\u6CD5\u6808\u968F\u7EBF\u7A0B\u800C\u751F\uFF0C\u4E5F\u968F\u7EBF\u7A0B\u800C\u706D\uFF1B\u6808\u5E27\u968F\u7740\u65B9\u6CD5\u7684\u5F00\u59CB\u800C\u5165\u6808\uFF0C\u968F\u7740\u65B9\u6CD5\u7684\u7ED3\u675F\u800C\u51FA\u6808\u3002\u8FD9\u51E0\u4E2A\u533A\u57DF\u7684\u5185\u5B58\u5206\u914D\u548C\u56DE\u6536\u90FD\u5177\u6709\u786E\u5B9A\u6027\uFF0C\u5728\u8FD9\u51E0\u4E2A\u533A\u57DF\u5185\u4E0D\u9700\u8981\u8FC7\u591A\u8003\u8651\u56DE\u6536\u7684\u95EE\u9898\uFF0C\u56E0\u4E3A\u65B9\u6CD5\u7ED3\u675F\u6216\u8005\u7EBF\u7A0B\u7ED3\u675F\u65F6\uFF0C\u5185\u5B58\u81EA\u7136\u5C31\u8DDF\u968F\u7740\u56DE\u6536\u4E86\u3002</p><p>\u800C\u5BF9\u4E8E Java \u5806\u548C\u65B9\u6CD5\u533A\uFF0C\u6211\u4EEC\u53EA\u6709\u5728\u7A0B\u5E8F\u8FD0\u884C\u671F\u95F4\u624D\u80FD\u77E5\u9053\u4F1A\u521B\u5EFA\u54EA\u4E9B\u5BF9\u8C61\uFF0C\u8FD9\u90E8\u5206\u5185\u5B58\u7684\u5206\u914D\u548C\u56DE\u6536\u90FD\u662F\u52A8\u6001\u7684\uFF0C\u5783\u573E\u6536\u96C6\u5668\u6240\u5173\u6CE8\u7684\u6B63\u662F\u8FD9\u90E8\u5206\u5185\u5B58\u3002</p><h2 id="\u5224\u5B9A\u5BF9\u8C61\u662F\u5426\u5B58\u6D3B" tabindex="-1"><a class="header-anchor" href="#\u5224\u5B9A\u5BF9\u8C61\u662F\u5426\u5B58\u6D3B" aria-hidden="true">#</a> \u5224\u5B9A\u5BF9\u8C61\u662F\u5426\u5B58\u6D3B</h2><p>\u82E5\u4E00\u4E2A\u5BF9\u8C61\u4E0D\u88AB\u4EFB\u4F55\u5BF9\u8C61\u6216\u53D8\u91CF\u5F15\u7528\uFF0C\u90A3\u4E48\u5B83\u5C31\u662F\u65E0\u6548\u5BF9\u8C61\uFF0C\u9700\u8981\u88AB\u56DE\u6536\u3002</p><h3 id="\u5F15\u7528\u8BA1\u6570\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5F15\u7528\u8BA1\u6570\u6CD5" aria-hidden="true">#</a> \u5F15\u7528\u8BA1\u6570\u6CD5</h3><p>\u5728\u5BF9\u8C61\u5934\u7EF4\u62A4\u7740\u4E00\u4E2A counter \u8BA1\u6570\u5668\uFF0C\u5BF9\u8C61\u88AB\u5F15\u7528\u4E00\u6B21\u5219\u8BA1\u6570\u5668 +1\uFF1B\u82E5\u5F15\u7528\u5931\u6548\u5219\u8BA1\u6570\u5668 -1\u3002\u5F53\u8BA1\u6570\u5668\u4E3A 0 \u65F6\uFF0C\u5C31\u8BA4\u4E3A\u8BE5\u5BF9\u8C61\u65E0\u6548\u4E86\u3002</p><p>\u5F15\u7528\u8BA1\u6570\u7B97\u6CD5\u7684\u5B9E\u73B0\u7B80\u5355\uFF0C\u5224\u5B9A\u6548\u7387\u4E5F\u5F88\u9AD8\uFF0C\u5728\u5927\u90E8\u5206\u60C5\u51B5\u4E0B\u5B83\u90FD\u662F\u4E00\u4E2A\u4E0D\u9519\u7684\u7B97\u6CD5\u3002\u4F46\u662F\u4E3B\u6D41\u7684 Java \u865A\u62DF\u673A\u91CC\u6CA1\u6709\u9009\u7528\u5F15\u7528\u8BA1\u6570\u7B97\u6CD5\u6765\u7BA1\u7406\u5185\u5B58\uFF0C\u4E3B\u8981\u662F\u56E0\u4E3A\u5B83\u5F88\u96BE\u89E3\u51B3\u5BF9\u8C61\u4E4B\u95F4\u5FAA\u73AF\u5F15\u7528\u7684\u95EE\u9898\u3002\uFF08\u867D\u7136\u5FAA\u73AF\u5F15\u7528\u7684\u95EE\u9898\u53EF\u901A\u8FC7 Recycler \u7B97\u6CD5\u89E3\u51B3\uFF0C\u4F46\u662F\u5728\u591A\u7EBF\u7A0B\u73AF\u5883\u4E0B\uFF0C\u5F15\u7528\u8BA1\u6570\u53D8\u66F4\u4E5F\u8981\u8FDB\u884C\u6602\u8D35\u7684\u540C\u6B65\u64CD\u4F5C\uFF0C\u6027\u80FD\u8F83\u4F4E\uFF0C\u65E9\u671F\u7684\u7F16\u7A0B\u8BED\u8A00\u4F1A\u91C7\u7528\u6B64\u7B97\u6CD5\u3002\uFF09</p><blockquote><p>\u4E3E\u4E2A\u6817\u5B50 \u{1F449} \u5BF9\u8C61 objA \u548C objB \u90FD\u6709\u5B57\u6BB5 instance\uFF0C\u4EE4 objA.instance = objB \u5E76\u4E14 objB.instance = objA\uFF0C\u7531\u4E8E\u5B83\u4EEC\u4E92\u76F8\u5F15\u7528\u7740\u5BF9\u65B9\uFF0C\u5BFC\u81F4\u5B83\u4EEC\u7684\u5F15\u7528\u8BA1\u6570\u90FD\u4E0D\u4E3A 0\uFF0C\u4E8E\u662F\u5F15\u7528\u8BA1\u6570\u7B97\u6CD5\u65E0\u6CD5\u901A\u77E5 GC \u6536\u96C6\u5668\u56DE\u6536\u5B83\u4EEC\u3002</p></blockquote><h3 id="\u53EF\u8FBE\u6027\u5206\u6790\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u53EF\u8FBE\u6027\u5206\u6790\u6CD5" aria-hidden="true">#</a> \u53EF\u8FBE\u6027\u5206\u6790\u6CD5</h3><p>\u6240\u6709\u548C GC Roots \u76F4\u63A5\u6216\u95F4\u63A5\u5173\u8054\u7684\u5BF9\u8C61\u90FD\u662F\u6709\u6548\u5BF9\u8C61\uFF0C\u548C GC Roots \u6CA1\u6709\u5173\u8054\u7684\u5BF9\u8C61\u5C31\u662F\u65E0\u6548\u5BF9\u8C61\u3002</p><p>GC Roots \u662F\u6307\uFF1A</p><ul><li>Java \u865A\u62DF\u673A\u6808\uFF08\u6808\u5E27\u4E2D\u7684\u672C\u5730\u53D8\u91CF\u8868\uFF09\u4E2D\u5F15\u7528\u7684\u5BF9\u8C61</li><li>\u672C\u5730\u65B9\u6CD5\u6808\u4E2D\u5F15\u7528\u7684\u5BF9\u8C61</li><li>\u65B9\u6CD5\u533A\u4E2D\u5E38\u91CF\u5F15\u7528\u7684\u5BF9\u8C61</li><li>\u65B9\u6CD5\u533A\u4E2D\u7C7B\u9759\u6001\u5C5E\u6027\u5F15\u7528\u7684\u5BF9\u8C61</li></ul><p>GC Roots \u5E76\u4E0D\u5305\u62EC\u5806\u4E2D\u5BF9\u8C61\u6240\u5F15\u7528\u7684\u5BF9\u8C61\uFF0C\u8FD9\u6837\u5C31\u4E0D\u4F1A\u6709\u5FAA\u73AF\u5F15\u7528\u7684\u95EE\u9898\u3002</p><h2 id="\u5F15\u7528\u7684\u79CD\u7C7B" tabindex="-1"><a class="header-anchor" href="#\u5F15\u7528\u7684\u79CD\u7C7B" aria-hidden="true">#</a> \u5F15\u7528\u7684\u79CD\u7C7B</h2><p>\u5224\u5B9A\u5BF9\u8C61\u662F\u5426\u5B58\u6D3B\u4E0E\u201C\u5F15\u7528\u201D\u6709\u5173\u3002\u5728 JDK 1.2 \u4EE5\u524D\uFF0CJava \u4E2D\u7684\u5F15\u7528\u5B9A\u4E49\u5F88\u4F20\u7EDF\uFF0C\u4E00\u4E2A\u5BF9\u8C61\u53EA\u6709\u88AB\u5F15\u7528\u6216\u8005\u6CA1\u6709\u88AB\u5F15\u7528\u4E24\u79CD\u72B6\u6001\uFF0C\u6211\u4EEC\u5E0C\u671B\u80FD\u63CF\u8FF0\u8FD9\u4E00\u7C7B\u5BF9\u8C61\uFF1A\u5F53\u5185\u5B58\u7A7A\u95F4\u8FD8\u8DB3\u591F\u65F6\uFF0C\u5219\u4FDD\u7559\u5728\u5185\u5B58\u4E2D\uFF1B\u5982\u679C\u5185\u5B58\u7A7A\u95F4\u5728\u8FDB\u884C\u5783\u573E\u6536\u96C6\u540E\u8FD8\u662F\u975E\u5E38\u7D27\u5F20\uFF0C\u5219\u53EF\u4EE5\u629B\u5F03\u8FD9\u4E9B\u5BF9\u8C61\u3002\u5F88\u591A\u7CFB\u7EDF\u7684\u7F13\u5B58\u529F\u80FD\u90FD\u7B26\u5408\u8FD9\u6837\u7684\u5E94\u7528\u573A\u666F\u3002</p><p>\u5728 JDK 1.2 \u4E4B\u540E\uFF0CJava \u5BF9\u5F15\u7528\u7684\u6982\u5FF5\u8FDB\u884C\u4E86\u6269\u5145\uFF0C\u5C06\u5F15\u7528\u5206\u4E3A\u4E86\u4EE5\u4E0B\u56DB\u79CD\u3002\u4E0D\u540C\u7684\u5F15\u7528\u7C7B\u578B\uFF0C\u4E3B\u8981\u4F53\u73B0\u7684\u662F\u5BF9\u8C61\u4E0D\u540C\u7684\u53EF\u8FBE\u6027\u72B6\u6001<code>reachable</code>\u548C\u5783\u573E\u6536\u96C6\u7684\u5F71\u54CD\u3002</p><h3 id="\u5F3A\u5F15\u7528-strong-reference" tabindex="-1"><a class="header-anchor" href="#\u5F3A\u5F15\u7528-strong-reference" aria-hidden="true">#</a> \u5F3A\u5F15\u7528\uFF08Strong Reference\uFF09</h3><p>\u7C7B\u4F3C &quot;<code>Object obj = new Object()</code>&quot; \u8FD9\u7C7B\u7684\u5F15\u7528\uFF0C\u5C31\u662F\u5F3A\u5F15\u7528\uFF0C\u53EA\u8981\u5F3A\u5F15\u7528\u5B58\u5728\uFF0C\u5783\u573E\u6536\u96C6\u5668\u6C38\u8FDC\u4E0D\u4F1A\u56DE\u6536\u88AB\u5F15\u7528\u7684\u5BF9\u8C61\u3002\u4F46\u662F\uFF0C\u5982\u679C\u6211\u4EEC<strong>\u9519\u8BEF\u5730\u4FDD\u6301\u4E86\u5F3A\u5F15\u7528</strong>\uFF0C\u6BD4\u5982\uFF1A\u8D4B\u503C\u7ED9\u4E86 static \u53D8\u91CF\uFF0C\u90A3\u4E48\u5BF9\u8C61\u5728\u5F88\u957F\u4E00\u6BB5\u65F6\u95F4\u5185\u4E0D\u4F1A\u88AB\u56DE\u6536\uFF0C\u4F1A\u4EA7\u751F\u5185\u5B58\u6CC4\u6F0F\u3002</p><h3 id="\u8F6F\u5F15\u7528-soft-reference" tabindex="-1"><a class="header-anchor" href="#\u8F6F\u5F15\u7528-soft-reference" aria-hidden="true">#</a> \u8F6F\u5F15\u7528\uFF08Soft Reference\uFF09</h3><p>\u8F6F\u5F15\u7528\u662F\u4E00\u79CD\u76F8\u5BF9\u5F3A\u5F15\u7528\u5F31\u5316\u4E00\u4E9B\u7684\u5F15\u7528\uFF0C\u53EF\u4EE5\u8BA9\u5BF9\u8C61\u8C41\u514D\u4E00\u4E9B\u5783\u573E\u6536\u96C6\uFF0C\u53EA\u6709\u5F53 JVM \u8BA4\u4E3A\u5185\u5B58\u4E0D\u8DB3\u65F6\uFF0C\u624D\u4F1A\u53BB\u8BD5\u56FE\u56DE\u6536\u8F6F\u5F15\u7528\u6307\u5411\u7684\u5BF9\u8C61\u3002JVM \u4F1A\u786E\u4FDD\u5728\u629B\u51FA OutOfMemoryError \u4E4B\u524D\uFF0C\u6E05\u7406\u8F6F\u5F15\u7528\u6307\u5411\u7684\u5BF9\u8C61\u3002\u8F6F\u5F15\u7528\u901A\u5E38\u7528\u6765<strong>\u5B9E\u73B0\u5185\u5B58\u654F\u611F\u7684\u7F13\u5B58</strong>\uFF0C\u5982\u679C\u8FD8\u6709\u7A7A\u95F2\u5185\u5B58\uFF0C\u5C31\u53EF\u4EE5\u6682\u65F6\u4FDD\u7559\u7F13\u5B58\uFF0C\u5F53\u5185\u5B58\u4E0D\u8DB3\u65F6\u6E05\u7406\u6389\uFF0C\u8FD9\u6837\u5C31\u4FDD\u8BC1\u4E86\u4F7F\u7528\u7F13\u5B58\u7684\u540C\u65F6\uFF0C\u4E0D\u4F1A\u8017\u5C3D\u5185\u5B58\u3002</p><h3 id="\u5F31\u5F15\u7528-weak-reference" tabindex="-1"><a class="header-anchor" href="#\u5F31\u5F15\u7528-weak-reference" aria-hidden="true">#</a> \u5F31\u5F15\u7528\uFF08Weak Reference\uFF09</h3><p>\u5F31\u5F15\u7528\u7684<strong>\u5F3A\u5EA6\u6BD4\u8F6F\u5F15\u7528\u66F4\u5F31</strong>\u4E00\u4E9B\u3002\u5F53 JVM \u8FDB\u884C\u5783\u573E\u56DE\u6536\u65F6\uFF0C<strong>\u65E0\u8BBA\u5185\u5B58\u662F\u5426\u5145\u8DB3\uFF0C\u90FD\u4F1A\u56DE\u6536</strong>\u53EA\u88AB\u5F31\u5F15\u7528\u5173\u8054\u7684\u5BF9\u8C61\u3002</p><h3 id="\u865A\u5F15\u7528-phantom-reference" tabindex="-1"><a class="header-anchor" href="#\u865A\u5F15\u7528-phantom-reference" aria-hidden="true">#</a> \u865A\u5F15\u7528\uFF08Phantom Reference\uFF09</h3><p>\u865A\u5F15\u7528\u4E5F\u79F0\u5E7D\u7075\u5F15\u7528\u6216\u8005\u5E7B\u5F71\u5F15\u7528\uFF0C\u5B83\u662F<strong>\u6700\u5F31</strong>\u7684\u4E00\u79CD\u5F15\u7528\u5173\u7CFB\u3002\u4E00\u4E2A\u5BF9\u8C61\u662F\u5426\u6709\u865A\u5F15\u7528\u7684\u5B58\u5728\uFF0C\u5B8C\u5168\u4E0D\u4F1A\u5BF9\u5176\u751F\u5B58\u65F6\u95F4\u6784\u6210\u5F71\u54CD\u3002\u5B83\u4EC5\u4EC5\u662F\u63D0\u4F9B\u4E86\u4E00\u79CD\u786E\u4FDD\u5BF9\u8C61\u88AB finalize \u4EE5\u540E\uFF0C\u505A\u67D0\u4E9B\u4E8B\u60C5\u7684\u673A\u5236\uFF0C\u6BD4\u5982\uFF0C\u901A\u5E38\u7528\u6765\u505A\u6240\u8C13\u7684 Post-Mortem \u6E05\u7406\u673A\u5236\u3002</p><h2 id="\u56DE\u6536\u5806\u4E2D\u65E0\u6548\u5BF9\u8C61" tabindex="-1"><a class="header-anchor" href="#\u56DE\u6536\u5806\u4E2D\u65E0\u6548\u5BF9\u8C61" aria-hidden="true">#</a> \u56DE\u6536\u5806\u4E2D\u65E0\u6548\u5BF9\u8C61</h2><p>\u5BF9\u4E8E\u53EF\u8FBE\u6027\u5206\u6790\u4E2D\u4E0D\u53EF\u8FBE\u7684\u5BF9\u8C61\uFF0C\u4E5F\u5E76\u4E0D\u662F\u6CA1\u6709\u5B58\u6D3B\u7684\u53EF\u80FD\u3002</p><h3 id="\u5224\u5B9A-finalize-\u662F\u5426\u6709\u5FC5\u8981\u6267\u884C" tabindex="-1"><a class="header-anchor" href="#\u5224\u5B9A-finalize-\u662F\u5426\u6709\u5FC5\u8981\u6267\u884C" aria-hidden="true">#</a> \u5224\u5B9A finalize() \u662F\u5426\u6709\u5FC5\u8981\u6267\u884C</h3><p>JVM \u4F1A\u5224\u65AD\u6B64\u5BF9\u8C61\u662F\u5426\u6709\u5FC5\u8981\u6267\u884C finalize() \u65B9\u6CD5\uFF0C\u5982\u679C\u5BF9\u8C61\u6CA1\u6709\u8986\u76D6 finalize() \u65B9\u6CD5\uFF0C\u6216\u8005 finalize() \u65B9\u6CD5\u5DF2\u7ECF\u88AB\u865A\u62DF\u673A\u8C03\u7528\u8FC7\uFF0C\u90A3\u4E48\u89C6\u4E3A\u201C\u6CA1\u6709\u5FC5\u8981\u6267\u884C\u201D\u3002\u90A3\u4E48\u5BF9\u8C61\u57FA\u672C\u4E0A\u5C31\u771F\u7684\u88AB\u56DE\u6536\u4E86\u3002</p><p>\u5982\u679C\u5BF9\u8C61\u88AB\u5224\u5B9A\u4E3A\u6709\u5FC5\u8981\u6267\u884C finalize() \u65B9\u6CD5\uFF0C\u90A3\u4E48\u5BF9\u8C61\u4F1A\u88AB\u653E\u5165\u4E00\u4E2A F-Queue \u961F\u5217\u4E2D\uFF0C\u865A\u62DF\u673A\u4F1A\u4EE5\u8F83\u4F4E\u7684\u4F18\u5148\u7EA7\u6267\u884C\u8FD9\u4E9B finalize()\u65B9\u6CD5\uFF0C\u4F46\u4E0D\u4F1A\u786E\u4FDD\u6240\u6709\u7684 finalize() \u65B9\u6CD5\u90FD\u4F1A\u6267\u884C\u7ED3\u675F\u3002\u5982\u679C finalize() \u65B9\u6CD5\u51FA\u73B0\u8017\u65F6\u64CD\u4F5C\uFF0C\u865A\u62DF\u673A\u5C31\u76F4\u63A5\u505C\u6B62\u6307\u5411\u8BE5\u65B9\u6CD5\uFF0C\u5C06\u5BF9\u8C61\u6E05\u9664\u3002</p><h3 id="\u5BF9\u8C61\u91CD\u751F\u6216\u6B7B\u4EA1" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u8C61\u91CD\u751F\u6216\u6B7B\u4EA1" aria-hidden="true">#</a> \u5BF9\u8C61\u91CD\u751F\u6216\u6B7B\u4EA1</h3><p>\u5982\u679C\u5728\u6267\u884C finalize() \u65B9\u6CD5\u65F6\uFF0C\u5C06 this \u8D4B\u7ED9\u4E86\u67D0\u4E00\u4E2A\u5F15\u7528\uFF0C\u90A3\u4E48\u8BE5\u5BF9\u8C61\u5C31\u91CD\u751F\u4E86\u3002\u5982\u679C\u6CA1\u6709\uFF0C\u90A3\u4E48\u5C31\u4F1A\u88AB\u5783\u573E\u6536\u96C6\u5668\u6E05\u9664\u3002</p><blockquote><p>\u4EFB\u4F55\u4E00\u4E2A\u5BF9\u8C61\u7684 finalize() \u65B9\u6CD5\u53EA\u4F1A\u88AB\u7CFB\u7EDF\u81EA\u52A8\u8C03\u7528\u4E00\u6B21\uFF0C\u5982\u679C\u5BF9\u8C61\u9762\u4E34\u4E0B\u4E00\u6B21\u56DE\u6536\uFF0C\u5B83\u7684 finalize() \u65B9\u6CD5\u4E0D\u4F1A\u88AB\u518D\u6B21\u6267\u884C\uFF0C\u60F3\u7EE7\u7EED\u5728 finalize() \u4E2D\u81EA\u6551\u5C31\u5931\u6548\u4E86\u3002</p></blockquote><h2 id="\u56DE\u6536\u65B9\u6CD5\u533A\u5185\u5B58" tabindex="-1"><a class="header-anchor" href="#\u56DE\u6536\u65B9\u6CD5\u533A\u5185\u5B58" aria-hidden="true">#</a> \u56DE\u6536\u65B9\u6CD5\u533A\u5185\u5B58</h2><p>\u65B9\u6CD5\u533A\u4E2D\u5B58\u653E\u751F\u547D\u5468\u671F\u8F83\u957F\u7684\u7C7B\u4FE1\u606F\u3001\u5E38\u91CF\u3001\u9759\u6001\u53D8\u91CF\uFF0C\u6BCF\u6B21\u5783\u573E\u6536\u96C6\u53EA\u6709\u5C11\u91CF\u7684\u5783\u573E\u88AB\u6E05\u9664\u3002\u65B9\u6CD5\u533A\u4E2D\u4E3B\u8981\u6E05\u9664\u4E24\u79CD\u5783\u573E\uFF1A</p><ul><li>\u5E9F\u5F03\u5E38\u91CF</li><li>\u65E0\u7528\u7684\u7C7B</li></ul><h3 id="\u5224\u5B9A\u5E9F\u5F03\u5E38\u91CF" tabindex="-1"><a class="header-anchor" href="#\u5224\u5B9A\u5E9F\u5F03\u5E38\u91CF" aria-hidden="true">#</a> \u5224\u5B9A\u5E9F\u5F03\u5E38\u91CF</h3><p>\u53EA\u8981\u5E38\u91CF\u6C60\u4E2D\u7684\u5E38\u91CF\u4E0D\u88AB\u4EFB\u4F55\u53D8\u91CF\u6216\u5BF9\u8C61\u5F15\u7528\uFF0C\u90A3\u4E48\u8FD9\u4E9B\u5E38\u91CF\u5C31\u4F1A\u88AB\u6E05\u9664\u6389\u3002\u6BD4\u5982\uFF0C\u4E00\u4E2A\u5B57\u7B26\u4E32 &quot;bingo&quot; \u8FDB\u5165\u4E86\u5E38\u91CF\u6C60\uFF0C\u4F46\u662F\u5F53\u524D\u7CFB\u7EDF\u6CA1\u6709\u4EFB\u4F55\u4E00\u4E2A String \u5BF9\u8C61\u5F15\u7528\u5E38\u91CF\u6C60\u4E2D\u7684 &quot;bingo&quot; \u5E38\u91CF\uFF0C\u4E5F\u6CA1\u6709\u5176\u5B83\u5730\u65B9\u5F15\u7528\u8FD9\u4E2A\u5B57\u9762\u91CF\uFF0C\u5FC5\u8981\u7684\u8BDD\uFF0C&quot;bingo&quot;\u5E38\u91CF\u4F1A\u88AB\u6E05\u7406\u51FA\u5E38\u91CF\u6C60\u3002</p><h3 id="\u5224\u5B9A\u65E0\u7528\u7684\u7C7B" tabindex="-1"><a class="header-anchor" href="#\u5224\u5B9A\u65E0\u7528\u7684\u7C7B" aria-hidden="true">#</a> \u5224\u5B9A\u65E0\u7528\u7684\u7C7B</h3><p>\u5224\u5B9A\u4E00\u4E2A\u7C7B\u662F\u5426\u662F\u201C\u65E0\u7528\u7684\u7C7B\u201D\uFF0C\u6761\u4EF6\u8F83\u4E3A\u82DB\u523B\u3002</p><ul><li>\u8BE5\u7C7B\u7684\u6240\u6709\u5BF9\u8C61\u90FD\u5DF2\u7ECF\u88AB\u6E05\u9664</li><li>\u52A0\u8F7D\u8BE5\u7C7B\u7684 ClassLoader \u5DF2\u7ECF\u88AB\u56DE\u6536</li><li>\u8BE5\u7C7B\u7684 java.lang.Class \u5BF9\u8C61\u6CA1\u6709\u5728\u4EFB\u4F55\u5730\u65B9\u88AB\u5F15\u7528\uFF0C\u65E0\u6CD5\u5728\u4EFB\u4F55\u5730\u65B9\u901A\u8FC7\u53CD\u5C04\u8BBF\u95EE\u8BE5\u7C7B\u7684\u65B9\u6CD5\u3002</li></ul><blockquote><p>\u4E00\u4E2A\u7C7B\u88AB\u865A\u62DF\u673A\u52A0\u8F7D\u8FDB\u65B9\u6CD5\u533A\uFF0C\u90A3\u4E48\u5728\u5806\u4E2D\u5C31\u4F1A\u6709\u4E00\u4E2A\u4EE3\u8868\u8BE5\u7C7B\u7684\u5BF9\u8C61\uFF1Ajava.lang.Class\u3002\u8FD9\u4E2A\u5BF9\u8C61\u5728\u7C7B\u88AB\u52A0\u8F7D\u8FDB\u65B9\u6CD5\u533A\u65F6\u521B\u5EFA\uFF0C\u5728\u65B9\u6CD5\u533A\u8BE5\u7C7B\u88AB\u5220\u9664\u65F6\u6E05\u9664\u3002</p></blockquote><h2 id="\u5783\u573E\u6536\u96C6\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5783\u573E\u6536\u96C6\u7B97\u6CD5" aria-hidden="true">#</a> \u5783\u573E\u6536\u96C6\u7B97\u6CD5</h2><p>\u5B66\u4F1A\u4E86\u5982\u4F55\u5224\u5B9A\u65E0\u6548\u5BF9\u8C61\u3001\u65E0\u7528\u7C7B\u3001\u5E9F\u5F03\u5E38\u91CF\u4E4B\u540E\uFF0C\u5269\u4F59\u5DE5\u4F5C\u5C31\u662F\u56DE\u6536\u8FD9\u4E9B\u5783\u573E\u3002\u5E38\u89C1\u7684\u5783\u573E\u6536\u96C6\u7B97\u6CD5\u6709\u4EE5\u4E0B\u51E0\u4E2A\uFF1A</p><h3 id="\u6807\u8BB0-\u6E05\u9664\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u6807\u8BB0-\u6E05\u9664\u7B97\u6CD5" aria-hidden="true">#</a> \u6807\u8BB0-\u6E05\u9664\u7B97\u6CD5</h3><p><strong>\u6807\u8BB0</strong>\u7684\u8FC7\u7A0B\u662F\uFF1A\u904D\u5386\u6240\u6709\u7684 <code>GC Roots</code>\uFF0C\u7136\u540E\u5C06\u6240\u6709 <code>GC Roots</code> \u53EF\u8FBE\u7684\u5BF9\u8C61<strong>\u6807\u8BB0\u4E3A\u5B58\u6D3B\u7684\u5BF9\u8C61</strong>\u3002</p><p><strong>\u6E05\u9664</strong>\u7684\u8FC7\u7A0B\u5C06\u904D\u5386\u5806\u4E2D\u6240\u6709\u7684\u5BF9\u8C61\uFF0C\u5C06\u6CA1\u6709\u6807\u8BB0\u7684\u5BF9\u8C61\u5168\u90E8\u6E05\u9664\u6389\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u6E05\u9664\u90A3\u4E9B\u88AB\u6807\u8BB0\u8FC7\u7684\u5BF9\u8C61\u7684\u6807\u8BB0\uFF0C\u4EE5\u4FBF\u4E0B\u6B21\u7684\u5783\u573E\u56DE\u6536\u3002</p><p>\u8FD9\u79CD\u65B9\u6CD5\u6709\u4E24\u4E2A<strong>\u4E0D\u8DB3</strong>\uFF1A</p><ul><li>\u6548\u7387\u95EE\u9898\uFF1A\u6807\u8BB0\u548C\u6E05\u9664\u4E24\u4E2A\u8FC7\u7A0B\u7684\u6548\u7387\u90FD\u4E0D\u9AD8\u3002</li><li>\u7A7A\u95F4\u95EE\u9898\uFF1A\u6807\u8BB0\u6E05\u9664\u4E4B\u540E\u4F1A\u4EA7\u751F\u5927\u91CF\u4E0D\u8FDE\u7EED\u7684\u5185\u5B58\u788E\u7247\uFF0C\u788E\u7247\u592A\u591A\u53EF\u80FD\u5BFC\u81F4\u4EE5\u540E\u9700\u8981\u5206\u914D\u8F83\u5927\u5BF9\u8C61\u65F6\uFF0C\u65E0\u6CD5\u627E\u5230\u8DB3\u591F\u7684\u8FDE\u7EED\u5185\u5B58\u800C\u4E0D\u5F97\u4E0D\u63D0\u524D\u89E6\u53D1\u53E6\u4E00\u6B21\u5783\u573E\u6536\u96C6\u52A8\u4F5C\u3002</li></ul><h3 id="\u590D\u5236\u7B97\u6CD5-\u65B0\u751F\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u590D\u5236\u7B97\u6CD5-\u65B0\u751F\u4EE3" aria-hidden="true">#</a> \u590D\u5236\u7B97\u6CD5\uFF08\u65B0\u751F\u4EE3\uFF09</h3><p>\u4E3A\u4E86\u89E3\u51B3\u6548\u7387\u95EE\u9898\uFF0C\u201C\u590D\u5236\u201D\u6536\u96C6\u7B97\u6CD5\u51FA\u73B0\u4E86\u3002\u5B83\u5C06\u53EF\u7528\u5185\u5B58\u6309\u5BB9\u91CF\u5212\u5206\u4E3A\u5927\u5C0F\u76F8\u7B49\u7684\u4E24\u5757\uFF0C\u6BCF\u6B21\u53EA\u4F7F\u7528\u5176\u4E2D\u7684\u4E00\u5757\u3002\u5F53\u8FD9\u4E00\u5757\u5185\u5B58\u7528\u5B8C\uFF0C\u9700\u8981\u8FDB\u884C\u5783\u573E\u6536\u96C6\u65F6\uFF0C\u5C31\u5C06\u5B58\u6D3B\u8005\u7684\u5BF9\u8C61\u590D\u5236\u5230\u53E6\u4E00\u5757\u4E0A\u9762\uFF0C\u7136\u540E\u5C06\u7B2C\u4E00\u5757\u5185\u5B58\u5168\u90E8\u6E05\u9664\u3002\u8FD9\u79CD\u7B97\u6CD5\u6709\u4F18\u6709\u52A3\uFF1A</p><ul><li>\u4F18\u70B9\uFF1A\u4E0D\u4F1A\u6709\u5185\u5B58\u788E\u7247\u7684\u95EE\u9898\u3002</li><li>\u7F3A\u70B9\uFF1A\u5185\u5B58\u7F29\u5C0F\u4E3A\u539F\u6765\u7684\u4E00\u534A\uFF0C\u6D6A\u8D39\u7A7A\u95F4\u3002</li></ul><p>\u4E3A\u4E86\u89E3\u51B3\u7A7A\u95F4\u5229\u7528\u7387\u95EE\u9898\uFF0C\u53EF\u4EE5\u5C06\u5185\u5B58\u5206\u4E3A\u4E09\u5757\uFF1A Eden\u3001From Survivor\u3001To Survivor\uFF0C\u6BD4\u4F8B\u662F 8:1:1\uFF0C\u6BCF\u6B21\u4F7F\u7528 Eden \u548C\u5176\u4E2D\u4E00\u5757 Survivor\u3002\u56DE\u6536\u65F6\uFF0C\u5C06 Eden \u548C Survivor \u4E2D\u8FD8\u5B58\u6D3B\u7684\u5BF9\u8C61\u4E00\u6B21\u6027\u590D\u5236\u5230\u53E6\u5916\u4E00\u5757 Survivor \u7A7A\u95F4\u4E0A\uFF0C\u6700\u540E\u6E05\u7406\u6389 Eden \u548C\u521A\u624D\u4F7F\u7528\u7684 Survivor \u7A7A\u95F4\u3002\u8FD9\u6837\u53EA\u6709 10% \u7684\u5185\u5B58\u88AB\u6D6A\u8D39\u3002</p><p>\u4F46\u662F\u6211\u4EEC\u65E0\u6CD5\u4FDD\u8BC1\u6BCF\u6B21\u56DE\u6536\u90FD\u53EA\u6709\u4E0D\u591A\u4E8E 10% \u7684\u5BF9\u8C61\u5B58\u6D3B\uFF0C\u5F53 Survivor \u7A7A\u95F4\u4E0D\u591F\uFF0C\u9700\u8981\u4F9D\u8D56\u5176\u4ED6\u5185\u5B58\uFF08\u6307\u8001\u5E74\u4EE3\uFF09\u8FDB\u884C\u5206\u914D\u62C5\u4FDD\u3002</p><h4 id="\u5206\u914D\u62C5\u4FDD" tabindex="-1"><a class="header-anchor" href="#\u5206\u914D\u62C5\u4FDD" aria-hidden="true">#</a> \u5206\u914D\u62C5\u4FDD</h4><p>\u4E3A\u5BF9\u8C61\u5206\u914D\u5185\u5B58\u7A7A\u95F4\u65F6\uFF0C\u5982\u679C Eden+Survivor \u4E2D\u7A7A\u95F2\u533A\u57DF\u65E0\u6CD5\u88C5\u4E0B\u8BE5\u5BF9\u8C61\uFF0C\u4F1A\u89E6\u53D1 MinorGC \u8FDB\u884C\u5783\u573E\u6536\u96C6\u3002\u4F46\u5982\u679C Minor GC \u8FC7\u540E\u4F9D\u7136\u6709\u8D85\u8FC7 10% \u7684\u5BF9\u8C61\u5B58\u6D3B\uFF0C\u8FD9\u6837\u5B58\u6D3B\u7684\u5BF9\u8C61\u76F4\u63A5\u901A\u8FC7\u5206\u914D\u62C5\u4FDD\u673A\u5236\u8FDB\u5165\u8001\u5E74\u4EE3\uFF0C\u7136\u540E\u518D\u5C06\u65B0\u5BF9\u8C61\u5B58\u5165 Eden \u533A\u3002</p><h3 id="\u6807\u8BB0-\u6574\u7406\u7B97\u6CD5-\u8001\u5E74\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u6807\u8BB0-\u6574\u7406\u7B97\u6CD5-\u8001\u5E74\u4EE3" aria-hidden="true">#</a> \u6807\u8BB0-\u6574\u7406\u7B97\u6CD5\uFF08\u8001\u5E74\u4EE3\uFF09</h3><p><strong>\u6807\u8BB0</strong>\uFF1A\u5B83\u7684\u7B2C\u4E00\u4E2A\u9636\u6BB5\u4E0E<strong>\u6807\u8BB0-\u6E05\u9664\u7B97\u6CD5</strong>\u662F\u4E00\u6A21\u4E00\u6837\u7684\uFF0C\u5747\u662F\u904D\u5386 <code>GC Roots</code>\uFF0C\u7136\u540E\u5C06\u5B58\u6D3B\u7684\u5BF9\u8C61\u6807\u8BB0\u3002</p><p><strong>\u6574\u7406</strong>\uFF1A\u79FB\u52A8\u6240\u6709<strong>\u5B58\u6D3B\u7684\u5BF9\u8C61</strong>\uFF0C\u4E14\u6309\u7167\u5185\u5B58\u5730\u5740\u6B21\u5E8F\u4F9D\u6B21\u6392\u5217\uFF0C\u7136\u540E\u5C06\u672B\u7AEF\u5185\u5B58\u5730\u5740\u4EE5\u540E\u7684\u5185\u5B58\u5168\u90E8\u56DE\u6536\u3002\u56E0\u6B64\uFF0C\u7B2C\u4E8C\u9636\u6BB5\u624D\u79F0\u4E3A\u6574\u7406\u9636\u6BB5\u3002</p><p>\u8FD9\u662F\u4E00\u79CD\u8001\u5E74\u4EE3\u7684\u5783\u573E\u6536\u96C6\u7B97\u6CD5\u3002\u8001\u5E74\u4EE3\u7684\u5BF9\u8C61\u4E00\u822C\u5BFF\u547D\u6BD4\u8F83\u957F\uFF0C\u56E0\u6B64\u6BCF\u6B21\u5783\u573E\u56DE\u6536\u4F1A\u6709\u5927\u91CF\u5BF9\u8C61\u5B58\u6D3B\uFF0C\u5982\u679C\u91C7\u7528\u590D\u5236\u7B97\u6CD5\uFF0C\u6BCF\u6B21\u9700\u8981\u590D\u5236\u5927\u91CF\u5B58\u6D3B\u7684\u5BF9\u8C61\uFF0C\u6548\u7387\u5F88\u4F4E\u3002</p><h3 id="\u5206\u4EE3\u6536\u96C6\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5206\u4EE3\u6536\u96C6\u7B97\u6CD5" aria-hidden="true">#</a> \u5206\u4EE3\u6536\u96C6\u7B97\u6CD5</h3><p>\u6839\u636E\u5BF9\u8C61\u5B58\u6D3B\u5468\u671F\u7684\u4E0D\u540C\uFF0C\u5C06\u5185\u5B58\u5212\u5206\u4E3A\u51E0\u5757\u3002\u4E00\u822C\u662F\u628A Java \u5806\u5206\u4E3A\u65B0\u751F\u4EE3\u548C\u8001\u5E74\u4EE3\uFF0C\u9488\u5BF9\u5404\u4E2A\u5E74\u4EE3\u7684\u7279\u70B9\u91C7\u7528\u6700\u9002\u5F53\u7684\u6536\u96C6\u7B97\u6CD5\u3002</p><ul><li>\u65B0\u751F\u4EE3\uFF1A\u590D\u5236\u7B97\u6CD5</li><li>\u8001\u5E74\u4EE3\uFF1A\u6807\u8BB0-\u6E05\u9664\u7B97\u6CD5\u3001\u6807\u8BB0-\u6574\u7406\u7B97\u6CD5</li></ul>',63);function n(o,h){return i}var t=e(r,[["render",n],["__file","03-gc-algorithms.html.vue"]]);export{t as default};
